// Game.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './Game.css'; // Importa el archivo CSS para estilos adicionales

const Game = () => {
  const { player1Name, player2Name } = useParams();
  const navigate = useNavigate();
  const [player1DeckId, setPlayer1DeckId] = useState('');
  const [player2DeckId, setPlayer2DeckId] = useState('');
  const [player1Cards, setPlayer1Cards] = useState([]);
  const [player2Cards, setPlayer2Cards] = useState([]);
  const [player1Sets, setPlayer1Sets] = useState([]);
  const [player2Sets, setPlayer2Sets] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const player1DeckResponse = await axios.get(
          'https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1'
        );
        const player2DeckResponse = await axios.get(
          'https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1'
        );
        const player1DrawResponse = await axios.get(
          `https://deckofcardsapi.com/api/deck/${player1DeckResponse.data.deck_id}/draw/?count=10`
        );
        const player2DrawResponse = await axios.get(
          `https://deckofcardsapi.com/api/deck/${player2DeckResponse.data.deck_id}/draw/?count=10`
        );

        setPlayer1DeckId(player1DeckResponse.data.deck_id);
        setPlayer2DeckId(player2DeckResponse.data.deck_id);
        setPlayer1Cards(player1DrawResponse.data.cards);
        setPlayer2Cards(player2DrawResponse.data.cards);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const organizeSets = (cards, setFunc) => {
      const sets = [];

      // Organizar en tercias o cuartas si son del mismo número
      const numberGroups = {};
      cards.forEach((card) => {
        if (!numberGroups[card.value]) {
          numberGroups[card.value] = [card];
        } else {
          numberGroups[card.value].push(card);
        }
      });

      Object.values(numberGroups).forEach((group) => {
        if (group.length === 3 || group.length === 4) {
          sets.push(group);
        }
      });

      // Organizar en escaleras si son del mismo tipo (escalera de valores consecutivos)
      const suitGroups = {};
      cards.forEach((card) => {
        if (!suitGroups[card.suit]) {
          suitGroups[card.suit] = [card];
        } else {
          suitGroups[card.suit].push(card);
        }
      });

      Object.values(suitGroups).forEach((group) => {
        const sortedGroup = group.sort((a, b) => {
          const values = ['2', '3', '4', '5', '6', '7', '8', '9', '10', 'JACK', 'QUEEN', 'KING', 'ACE'];
          return values.indexOf(a.value) - values.indexOf(b.value);
        });

        let isStraight = true;
        for (let i = 0; i < sortedGroup.length - 1; i++) {
          const currentCardValue = sortedGroup[i].value;
          const nextCardValue = sortedGroup[i + 1].value;
          const currentIndex = values.indexOf(currentCardValue);
          const nextIndex = values.indexOf(nextCardValue);

          if (nextIndex - currentIndex !== 1) {
            isStraight = false;
            break;
          }
        }

        if (isStraight) {
          sets.push(sortedGroup);
        }
      });

      setFunc(sets);
    };

    organizeSets(player1Cards, setPlayer1Sets);
    organizeSets(player2Cards, setPlayer2Sets);
  }, [player1Cards, player2Cards]);

  const handleFinishGame = () => {
    navigate('/');
  };
  

  return (
    <div>
      <h2>{player1Name}</h2>
      <p>Deck ID: {player1DeckId}</p>
      <div className="card-container">
        {player1Sets.map((set, index) => (
          <div key={index} className="set">
            {set.map((card) => (
              <img
                key={card.code}
                src={card.image}
                alt={`${card.value} of ${card.suit}`}
                className="card"
              />
            ))}
          </div>
        ))}
      </div>

      <h2>{player2Name}</h2>
      <p>Deck ID: {player2DeckId}</p>
      <div className="card-container">
        {player2Sets.map((set, index) => (
          <div key={index} className="set">
            {set.map((card) => (
              <img
                key={card.code}
                src={card.image}
                alt={`${card.value} of ${card.suit}`}
                className="card"
              />
            ))}
          </div>
        ))}
      </div>

      <button onClick={handleFinishGame}>Terminar juego</button>
    </div>
  );
};

export default Game;
