import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './Game.css';

const Game = () => {
  const { player1Name, player2Name } = useParams();
  const navigate = useNavigate();
  const [player1DeckId, setPlayer1DeckId] = useState('');
  const [player2DeckId, setPlayer2DeckId] = useState('');
  const [player1Cards, setPlayer1Cards] = useState([]);
  const [player2Cards, setPlayer2Cards] = useState([]);
  const [player1Sets, setPlayer1Sets] = useState([]);
  const [player2Sets, setPlayer2Sets] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const player1DeckResponse = await axios.get(
          'https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1'
        );
        const player2DeckResponse = await axios.get(
          'https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1'
        );
        const player1DrawResponse = await axios.get(
          `https://deckofcardsapi.com/api/deck/${player1DeckResponse.data.deck_id}/draw/?count=10`
        );
        const player2DrawResponse = await axios.get(
          `https://deckofcardsapi.com/api/deck/${player2DeckResponse.data.deck_id}/draw/?count=10`
        );

        setPlayer1DeckId(player1DeckResponse.data.deck_id);
        setPlayer2DeckId(player2DeckResponse.data.deck_id);
        setPlayer1Cards(player1DrawResponse.data.cards);
        setPlayer2Cards(player2DrawResponse.data.cards);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const organizeSets = (cards, setFunc) => {
      const values = ['2', '3', '4', '5', '6', '7', '8', '9', '10', 'JACK', 'QUEEN', 'KING', 'ACE'];
      const suits = ['HEARTS', 'DIAMONDS', 'CLUBS', 'SPADES'];

      const cardsWithInfo = cards.map((card) => {
        const valueIndex = values.indexOf(card.value);
        const suitIndex = suits.indexOf(card.suit);
        return {
          ...card,
          valueIndex,
          suitIndex
        };
      });

      const sortedCards = [...cardsWithInfo].sort((a, b) => {
        return a.valueIndex - b.valueIndex;
      });

      const setsBySuit = suits.map(() => []);
      let currentSet = [];

      sortedCards.forEach((card) => {
        if (currentSet.length === 0) {
          currentSet.push(card);
        } else {
          const prevCard = currentSet[currentSet.length - 1];

          if (prevCard.valueIndex === card.valueIndex - 1 && prevCard.suitIndex === card.suitIndex) {
            currentSet.push(card);
          } else {
            setsBySuit[prevCard.suitIndex].push(currentSet);
            currentSet = [card];
          }
        }
      });

      if (currentSet.length > 0) {
        setsBySuit[currentSet[0].suitIndex].push(currentSet);
      }

      setsBySuit.forEach((setsInSuit) => {
        setsInSuit.forEach((set) => {
          if (set.length >= 2) {
            setFunc((prevSets) => [...prevSets, set]);
          }
        });
      });
    };

    organizeSets(player1Cards, setPlayer1Sets);
    organizeSets(player2Cards, setPlayer2Sets);
  }, [player1Cards, player2Cards]);

  const handleFinishGame = () => {
    navigate('/');
  };

  const analyzeAndDiscard = (cards, newCard, setFunc) => {
    const values = ['2', '3', '4', '5', '6', '7', '8', '9', '10', 'JACK', 'QUEEN', 'KING', 'ACE'];
    const suits = ['HEARTS', 'DIAMONDS', 'CLUBS', 'SPADES'];

    const cardsWithInfo = cards.map((card) => {
      const valueIndex = values.indexOf(card.value);
      const suitIndex = suits.indexOf(card.suit);
      return {
        ...card,
        valueIndex,
        suitIndex
      };
    });

    const newCardInfo = {
      ...newCard,
      valueIndex: values.indexOf(newCard.value),
      suitIndex: suits.indexOf(newCard.suit)
    };

    const sortedCards = [...cardsWithInfo, newCardInfo].sort((a, b) => {
      return a.valueIndex - b.valueIndex;
    });

    const setsBySuit = suits.map(() => []);
    let currentSet = [];

    sortedCards.forEach((card) => {
      if (currentSet.length === 0) {
        currentSet.push(card);
      } else {
        const prevCard = currentSet[currentSet.length - 1];

        if (prevCard.valueIndex === card.valueIndex - 1 && prevCard.suitIndex === card.suitIndex) {
          currentSet.push(card);
        } else {
          setsBySuit[prevCard.suitIndex].push(currentSet);
          currentSet = [card];
        }
      }
    });

    if (currentSet.length > 0) {
      setsBySuit[currentSet[0].suitIndex].push(currentSet);
    }

    let discardedCard = null;

    setsBySuit.forEach((setsInSuit) => {
      setsInSuit.forEach((set) => {
        if (set.length >= 3) {
          setFunc((prevCards) => {
            const updatedCards = prevCards.filter((card) => !set.includes(card));
            discardedCard = set[0];
            return [...updatedCards, newCardInfo];
          });
        }
      });
    });

    return discardedCard;
  };

  const handleDrawCard = async () => {
    try {
      const player1DrawResponse = await axios.get(
        `https://deckofcardsapi.com/api/deck/${player1DeckId}/draw/?count=1`
      );
      const player2DrawResponse = await axios.get(
        `https://deckofcardsapi.com/api/deck/${player2DeckId}/draw/?count=1`
      );

      const drawnCard1 = player1DrawResponse.data.cards[0];
      const drawnCard2 = player2DrawResponse.data.cards[0];

      const discardedCard1 = analyzeAndDiscard(player1Cards, drawnCard1, setPlayer1Cards);
      const discardedCard2 = analyzeAndDiscard(player2Cards, drawnCard2, setPlayer2Cards);

      if (discardedCard1) {
        const player1AddCardResponse = await axios.get(
          `https://deckofcardsapi.com/api/deck/${player1DeckId}/pile/discard/add/?cards=${discardedCard1.code}`
        );
        console.log(player1AddCardResponse.data);
      }

      if (discardedCard2) {
        const player2AddCardResponse = await axios.get(
          `https://deckofcardsapi.com/api/deck/${player2DeckId}/pile/discard/add/?cards=${discardedCard2.code}`
        );
        console.log(player2AddCardResponse.data);
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <h2>{player1Name}</h2>
      <p>Deck ID: {player1DeckId}</p>
      <div className="card-container">
        {player1Sets.map((set, index) => (
          <div key={index} className="set">
            {set.map((card) => (
              <img
                key={card.code}
                src={card.image}
                alt={`${card.value} of ${card.suit}`}
                className="card"
              />
            ))}
          </div>
        ))}
      </div>

      <h2>{player2Name}</h2>
      <p>Deck ID: {player2DeckId}</p>
      <div className="card-container">
        {player2Sets.map((set, index) => (
          <div key={index} className="set">
            {set.map((card) => (
              <img
                key={card.code}
                src={card.image}
                alt={`${card.value} of ${card.suit}`}
                className="card"
              />
            ))}
          </div>
        ))}
      </div>

      <button onClick={handleDrawCard}>Pedir carta</button>
      <button onClick={handleFinishGame}>Terminar juego</button>
    </div>
  );
};

export default Game;
